import{_ as c,a as t}from"./chunks/logo-4.DpA8tASZ.js";import{_ as s,c as e,o as i,a4 as d}from"./chunks/framework.t-GQSDtj.js";const m=JSON.parse('{"title":"TensorPlay 生态系统","description":"","frontmatter":{"layout":"page","title":"TensorPlay 生态系统"},"headers":[],"relativePath":"zh/ecosystem/index.md","filePath":"zh/ecosystem/index.md"}'),l={name:"zh/ecosystem/index.md"};function r(o,a,n,f,v,p){return i(),e("div",null,[...a[0]||(a[0]=[d('<div class="torch-style-page" data-v-fc5bc844><div class="main-content" data-v-fc5bc844><h1 class="hero-title" data-v-fc5bc844>TensorPlay</h1><p class="hero-lead" data-v-fc5bc844> TensorPlay 是一个开源深度学习框架，专为教育和研究而设计，具有灵活和模块化的特点，提供了 AI 系统内部机制的清晰视图。 </p><div class="description-text" data-v-fc5bc844><p data-v-fc5bc844> TensorPlay 简化了从理论理解到实践实现的路径。TensorPlay 旨在提供最大的清晰度和可破解性，支持动态计算图，使学生和研究人员能够直观地探索神经网络内部。其 Pythonic 设计以及与原生 Python 工具的深度集成，使其成为掌握深度学习概念的易用平台。 </p><p data-v-fc5bc844> TensorPlay 专为教育和透明度而设计，是理解框架底层工作原理的理想游乐场。通过模块化的库生态系统，它支持从基础张量操作到构建自定义神经网络层的基本用例。TensorPlay 提供了 CPU 和 CUDA 后端的可读实现，非常适合学习如何编写自定义硬件内核并使用 nanobind 进行绑定。 </p><p data-v-fc5bc844> 无论你是正在学习深度学习基础知识，还是在通过原型验证新的架构想法，TensorPlay 都能提供将好奇心转化为知识所需的简洁性、透明度和灵活性。 </p></div><a href="/zh/guide/getting-started" class="get-started-btn" data-v-fc5bc844>开始使用 TensorPlay</a><div class="ecosystem-pillars" data-v-fc5bc844><h2 style="font-size:2rem;margin-bottom:2rem;border-bottom:1px solid var(--vp-c-divider);padding-bottom:0.5rem;font-weight:600;" data-v-fc5bc844>生态系统支柱</h2><div class="pillar-grid" data-v-fc5bc844><a href="/zh/guide/architecture#1-p10-tensor-computation-engine" class="pillar-item" data-v-fc5bc844><span class="pillar-tag core" data-v-fc5bc844>CORE</span><h3 data-v-fc5bc844>P10</h3><p data-v-fc5bc844>用于纯张量操作的基础计算引擎。</p></a><a href="/zh/guide/architecture#2-tpx-autograd-engine" class="pillar-item" data-v-fc5bc844><span class="pillar-tag capacity" data-v-fc5bc844>CAPACITY</span><h3 data-v-fc5bc844>TPX</h3><p data-v-fc5bc844>用于动态图的显式自动微分层。</p></a><a href="/zh/guide/architecture#3-stax-static-graph-accelerator" class="pillar-item" data-v-fc5bc844><span class="pillar-tag capacity" data-v-fc5bc844>CAPACITY</span><h3 data-v-fc5bc844>Stax</h3><p data-v-fc5bc844>静态图优化和即时 (JIT) 编译。</p></a><a href="/zh/guide/architecture#4-nn-neural-network-library" class="pillar-item" data-v-fc5bc844><span class="pillar-tag business" data-v-fc5bc844>API</span><h3 data-v-fc5bc844>NN</h3><p data-v-fc5bc844>兼容 PyTorch 的高级神经网络 API。</p></a></div></div></div><div class="side-panel" data-v-fc5bc844><div class="project-logo" data-v-fc5bc844><img src="'+c+'" alt="TensorPlay Logo" class="light-only" data-v-fc5bc844><img src="'+t+'" alt="TensorPlay Logo" class="dark-only" data-v-fc5bc844></div><div class="external-links" data-v-fc5bc844><a href="https://github.com/bluemoon-o2/tensorplay" target="_blank" title="GitHub" data-v-fc5bc844><div class="icon-circle" data-v-fc5bc844><i class="fa-brands fa-github" data-v-fc5bc844></i></div></a><a href="/zh/blog/" title="博客与新闻" data-v-fc5bc844><div class="icon-circle" data-v-fc5bc844><i class="fa-solid fa-rss" data-v-fc5bc844></i></div></a><a href="/zh/guide/resources" title="文档" data-v-fc5bc844><div class="icon-circle" data-v-fc5bc844><i class="fa-solid fa-file-lines" data-v-fc5bc844></i></div></a><a href="/zh/community/" title="社区" data-v-fc5bc844><div class="icon-circle" data-v-fc5bc844><i class="fa-solid fa-users" data-v-fc5bc844></i></div></a></div></div></div>',1)])])}const g=s(l,[["render",r],["__scopeId","data-v-fc5bc844"]]);export{m as __pageData,g as default};
